    Cookies.set("userEmail", email);
    Cookies.set("userToken", token);
  };
  render() {
    return (
      <BrowserRouter>
        <>
          <Header />
          <Switch>
            <Route
              exact={true}
              path="/"
              render={props => <Home {...props} />}
            />
            <Route
              path="/offer/:offerId"
              render={props => <Offer {...props} />}
            />
            <Route
              // exact={true}
              path="/sign_up"
              render={props => <SignUp setUserId={this.setUserId} {...props} />}
            />
            <Route path="/log_in" render={props => <LogIn {...props} />} />
          </Switch>
        </>
      </BrowserRouter>
    );
  }
}

export default App;
